<script>

export default {
  data: () => ({
    count: 0,
    num: 1000,
  }),
  watch: {
    num (val) {
      this.setCount(val)
    }
  },
  methods: {
    addVal (n) {
      this.num += n
    },
    resetVal () {
      this.num = 0
      this.count = 0
    },
    setCount (val) {
      const obj = { n: this.count }
      this.$anime({
        targets: obj,
        n: val,
        round: 1,
        duration: 500,
        easing: 'linear',
        update: () => {
          this.count = obj.n
        }
      })
    }
  },
  mounted () {
    this.setCount(this.num);
  }
}
</script>

